import React, { useState, useEffect } from 'react'
import { supabase } from './supabaseClient'

function App() {
  const [roomName, setRoomName] = useState('')
  const [userName, setUserName] = useState('')
  const [isInRoom, setIsInRoom] = useState(false)
  const [newRoomName, setNewRoomName] = useState('')
  const [newUserName, setNewUserName] = useState('')
  const [songs, setSongs] = useState([])
  const [newSong, setNewSong] = useState('')
  const [suggestions, setSuggestions] = useState([])

  useEffect(() => {
    if (!isInRoom) return

    loadSongs()

    const channel = supabase
      .channel('public:songs')
      .on(
        'postgres_changes',
        { event: '*', schema: 'public', table: 'songs', filter: `room_id=eq.${roomName}` },
        () => loadSongs()
      )
      .subscribe()

    return () => {
      supabase.removeChannel(channel)
    }
  }, [isInRoom, roomName])

  const loadSongs = async () => {
    if (!roomName) return

    const { data, error } = await supabase
      .from('songs')
      .select('*')
      .eq('room_id', roomName)
      .order('votes', { ascending: false })

    if (error) {
      console.error('Error loading songs:', error)
    } else {
      setSongs(data)
    }
  }

  const fetchSuggestions = async (query) => {
    if (!query) {
      setSuggestions([])
      return
    }

    try {
      const response = await fetch(`http://localhost:3001/search?q=${encodeURIComponent(query)}`)
      const data = await response.json()
      if (data && data.tracks && Array.isArray(data.tracks.items)) {
        setSuggestions(data.tracks.items)
      } else {
        console.warn('Invalid suggestions format:', data)
        setSuggestions([])
      }
    } catch (error) {
      console.error('Error fetching suggestions:', error)
      setSuggestions([])
    }
  }

  const handleAddSong = async (title = newSong) => {
    if (!title) return

    const { error } = await supabase.from('songs').insert({
      title,
      room_id: roomName,
      votes: 0,
    })

    if (error) {
      console.error('Error adding song:', error)
    } else {
      setNewSong('')
      setSuggestions([])
      await loadSongs()
      console.log('Song added and queue updated')
    }
  }


  const handleJoinRoom = () => {
    if (!newRoomName || !newUserName) return
    setRoomName(newRoomName)
    setUserName(newUserName)
    setIsInRoom(true)
  }

  const handleVote = async (songId, delta) => {
    const { data, error: fetchError } = await supabase
      .from('songs')
      .select('votes')
      .eq('id', songId)
      .single()

    if (fetchError) {
      console.error('Error fetching votes:', fetchError)
      return
    }

    const currentVotes = data.votes || 0

    const { error: updateError } = await supabase
      .from('songs')
      .update({ votes: currentVotes + delta })
      .eq('id', songId)

    if (updateError) {
      console.error('Error voting:', updateError)
    } else {
      await loadSongs()
    }
  }

  if (!isInRoom) {
    return (
      <div style={{ padding: 40 }}>
        <h1>üé∂ Hang The DJ</h1>
        <input
          type="text"
          placeholder="Room name"
          value={newRoomName}
          onChange={(e) => setNewRoomName(e.target.value)}
          style={{ marginBottom: 10, display: 'block' }}
        />
        <input
          type="text"
          placeholder="Your name"
          value={newUserName}
          onChange={(e) => setNewUserName(e.target.value)}
          style={{ marginBottom: 10, display: 'block' }}
        />
        <button onClick={handleJoinRoom}>Join</button>
      </div>
    )
  }

  return (
    <div style={{ padding: 40 }}>
      <h2>üéâ You are in room "{roomName}"</h2>
      <p>Your nickname: {userName}</p>

      <h3>Add song to queue:</h3>
      <input
        type="text"
        value={newSong}
        onChange={(e) => {
          setNewSong(e.target.value)
          fetchSuggestions(e.target.value)
        }}
        placeholder="Type to search"
        style={{ width: '300px' }}
      />
      <button onClick={handleAddSong}>Add</button>

      {suggestions.length > 0 && (
        <ul style={{ listStyle: 'none', padding: 0 }}>
          {suggestions.map((track) => (
            <li
              key={track.id}
              style={{
                cursor: 'pointer',
                marginTop: 5,
                background: '#f5f5f5',
                padding: 10,
                borderRadius: 4,
              }}
               onClick={() => handleAddSong(`${track.name} ‚Äì ${track.artists.map((a) => a.name).join(', ')}`)}
                style={{ cursor: 'pointer', margin: '4px 0', background: '#eee', padding: '4px' }}
              >
               {track.name} ‚Äì {track.artists.map((a) => a.name).join(', ')}
              </li>
            ))}
        </ul>
      )}

      <h3>üéµ Song queue:</h3>
      <p>Total songs: {songs.length}</p>
      <ul>
        {songs.map((song) => (
          <li key={song.id}>
            {song.title} ‚Äî {song.votes} votes
            <button onClick={() => handleVote(song.id, 1)}>üëç</button>
            <button onClick={() => handleVote(song.id, -1)}>üëé</button>
          </li>
        ))}
      </ul>
    </div>
  )
}

export default App
